var login_btn=document.getElementById("loginff");login_btn.addEventListener("click",function(){var e=document.getElementById("user_name").value,t=document.getElementById("user_password").value,n=new Array(localStorage.length);for(i=0;i<localStorage.length;i++)n[i]=localStorage.key(i);if(n.includes(e)){var l=localStorage.getItem(e);document.getElementById("login_sucss").innerHTML=t==l?"登录成功!点击这里回到首页":"密码错误！"}else document.getElementById("login_sucss").innerHTML="用户名不存在!"});var register_btn=document.getElementById("register_btn");register_btn.addEventListener("click",function(){var e=document.getElementById("user_name").value,t=document.getElementById("user_password").value;0==e.length||0==t.length?alert("请输入需要注册的用户名或密码！"):(localStorage.setItem(e,t),alert("注册成功!"))});